package qa_project_javapackage;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =10;
		int b=20;
		int sum=a+b;
		int sub=b-a;
		int mult=a*b;
		int div=b/a;
		
		int mod=b%a;
		
		System.out.println("The sum of two number is "+sum);
		System.out.println("The result of two numbers is "+sub);
		System.out.println("The result of two numbers is "+mult);
		System.out.println("The result of two numbers is "+div);
		System.out.println("The reminder value "+mod);
		
	}

}
