package qa_project_javapackage_2;

public class ConditionalStatements_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int a = 100;
			
			if(a%2 ==0) 
			{
				System.out.println("Then the number is even  " +a);
			}
	
			else 
			{
				System.out.println("Then the number is odd  "+a);
			}
	}

}
