package qa_project_javapackage_2;

public class DataTypes_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 30;
		int b = 10;
		int sum = a+b;
		int sub = a-b;
		int mult = a*b;
		int div = a/b;
		
		int mod = a%b;

		System.out.println("If you add a and b, then you will get "+sum);
		System.out.println("If you subtract b from a, then you will get "+sub);
		System.out.println("If you multiply a x b, then you will get "+mult);
		System.out.println("If you divide a by b, then you will get "+div);
		System.out.println("The remainder of a divided by b is zero " +mod);
	}

}
