package qa_project_javapackage_2;

public class EvenOddLoop_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0;i<=10;i++)
		{
			if(i%2==0)
			{
				System.out.println("The number is even");
			}
			
			else
			
			{
				System.out.println("The number is odd");
			}
		}
	}

}
