package qa_project_javapackage_4;

public class EvenOddLoop_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=2; i<=100;i++)
			
			if(i%2==1)
			{
				System.out.println("This is correct");
			
			}
		
			else
				
			{
				System.out.println("This is not correct");
			}
	}

}
